window.onload=()=>{const e=undefined,o=.96*(document.querySelector("nav").getBoundingClientRect().height||60),t=e=>{if(!e)return;const t=document.querySelector(e);if(t){const e=t.getBoundingClientRect().top+window.scrollY-o;window.scrollTo({top:e,behavior:"smooth"})}};document.querySelectorAll('a[href^="#"]').forEach((e=>e.addEventListener("click",(o=>{o.preventDefault(),t(e.getAttribute("href"))})))),t(window.location.hash),window.addEventListener("hashchange",(()=>{t(window.location.hash)}))};