document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelectorAll("[data-src]"),t=undefined;if("IntersectionObserver"in window){const t=new IntersectionObserver(((e,t)=>{e.forEach((e=>{if(e.isIntersecting){const r=e.target;r.src=r.dataset.src,r.classList.add("loaded"),t.unobserve(r)}}))}),{rootMargin:"0px 0px 50px 0px",threshold:.01});e.forEach((e=>t.observe(e)))}else e.forEach((e=>e.src=e.dataset.src))}));