window.onload=()=>{const e=document.querySelector("nav"),t=e?e.offsetHeight:0,o="scrollBehavior"in document.documentElement.style;document.querySelectorAll('a[href^="#"]').forEach((e=>{e.addEventListener("click",(n=>{n.preventDefault();const c=document.querySelector(e.getAttribute("href"));if(c){const e=c.getBoundingClientRect().top+window.pageYOffset-t;window.scrollTo({top:e,behavior:o?"smooth":"auto"})}}))}))};