window.onload=()=>{const e="scrollBehavior"in document.documentElement.style;document.querySelectorAll('a[href^="#"]').forEach((t=>{t.addEventListener("click",(o=>{o.preventDefault();const n=60,c=document.querySelector(t.getAttribute("href"));if(c){const t=c.getBoundingClientRect().top+window.pageYOffset-n;window.scrollTo({top:t,behavior:e?"smooth":"auto"})}}))}))};