window.onload=()=>{const e="scrollBehavior"in document.documentElement.style;document.querySelectorAll('a[href^="#"]').forEach((t=>{t.addEventListener("click",(o=>{o.preventDefault();const n=undefined,c=document.querySelector("nav").offsetHeight,r=document.querySelector(t.getAttribute("href"));if(r){const t=r.getBoundingClientRect().top+window.pageYOffset-c;window.scrollTo({top:t,behavior:e?"smooth":"auto"})}}))}))};