window.onload=()=>{const e=document.querySelector("nav"),t=e?e.offsetHeight:60,o="scrollBehavior"in document.documentElement.style;document.querySelectorAll('a[href^="#"]').forEach((e=>{e.addEventListener("click",(n=>{n.preventDefault();const r=document.querySelector(e.getAttribute("href"));if(r){const e=r.getBoundingClientRect().top+window.pageYOffset-t;window.scrollTo({top:e,behavior:o?"smooth":"auto"})}else console.warn(`Element not found for anchor: ${e.getAttribute("href")}`)}))}))};